import React, { useState, Fragment, useEffect } from 'react';
import { NextPage } from 'next';
import Head from 'next/head';
import { AiFillCloseSquare } from 'react-icons/ai';
import { IoIosEye } from 'react-icons/io';
import { Row, Col } from 'react-flexbox-grid';
import { Tag } from 'baseui/tag';
import { Block } from 'baseui/block';
import { Button } from 'baseui/button';
import { StyledTable, StyledBodyCell } from 'baseui/table-grid';
import { Modal, ModalHeader, ModalBody } from 'baseui/modal';
import { FlexGrid, FlexGridItem } from 'baseui/flex-grid';
import { FormControl } from 'baseui/form-control';
import { Input } from 'baseui/input';
import { Textarea } from 'baseui/textarea';
import { Select } from 'baseui/select';
import Container from '../../components/UiElements/Container/Container';
import AppsMenu from '../../components/SideMenu/AppsMenu';
import IconButton from '../../components/UiElements/IconButton/IconButton';
import { StyledTableHeadAlt } from '../../components/PageStyles/Apps.styled';
import { firebaseCRUDPageData } from '../../data/appsData';
import { articleType, articlesType } from '../../types';
import FirebaseCrud from '../../containers/Crud/Crud';
import {
  getDocuments,
  addDocument,
  updateDocument,
  deleteDocument,
} from '../../firebase/service';

const statusOptions = [
  { label: 'Draft', id: 'draft' },
  { label: 'Publish', id: 'publish' },
];

const FirebaseCRUD: NextPage<{}> = () => {
  const { title, subtitle } = firebaseCRUDPageData;
  const [articles, setArticles] = useState([]);
  const [visible, setVisible] = useState(false);
  const [editState, setEditState] = useState(false);
  const [article, setArticle] = useState({
    id: null,
    title: '',
    description: '',
    slug: '',
    status: [],
  });
  useEffect(() => {
    fetchData();
  }, []);
  const fetchData = async () => {
    // let data: articlesType;
    const data = await getDocuments('articles');
    console.log(data, 'data data');
    if (data && data.length) {
      console.log(data);
      // data = queryData;
      setArticles(data);
    }
  };
  const handleAddArticle = () => {
    setVisible(true);
    setEditState(false);
  };

  const handleModdalClose = () => {
    setVisible(false);
    setArticle({
      id: null,
      title: '',
      description: '',
      slug: '',
      status: [],
    });
  };

  const handleDeleteArticle = async (id: string) => {
    console.log(id, 'what id');
    if (id) {
      const deleted = await deleteDocument('articles', id);
      if (deleted) {
        fetchData();
      }
    }
  };

  const handleUpdateArticle = async (item: any) => {
    setVisible(true);
    setEditState(true);
    const index = statusOptions.findIndex(option => option.id === item.status);
    let status = { label: 'Draft', id: 'draft' };
    if (index > -1) {
      status = statusOptions[index];
    }
    setArticle({
      ...article,
      ...item,
      status,
    });
    console.log(item, 'item');
  };

  const handleOnChange = (name: string) => (e: any) => {
    let value: any;
    if (name === 'status') {
      value = e.value;
    } else {
      value = e.target.value;
    }

    setArticle({
      ...article,
      [name]: value,
    });
  };

  const handleOnSubmit = async () => {
    console.log('Form submit', article);
    // const slug = `${article.slug}-${Date.now()}`;
    const status =
      article.status && article.status.length ? article.status[0].id : 'draft';
    const slug = article.id ? article.slug : `${article.slug}-${Date.now()}`;
    const data = {
      ...article,
      slug,
      status,
    };
    let id = null;
    if (article.id) {
      id = await updateDocument('articles', data);
    } else {
      id = await addDocument('articles', data);
    }
    console.log(id, 'what id');
    if (id) {
      fetchData();
      setVisible(false);
    }
  };

  return (
    <>
      <Head>
        <title>{title} | INST.</title>
        <meta name="Description" content={subtitle} />
      </Head>

      <Container>
        <Block paddingTop={['15px', '20px', '30px', '40px']}>
          <Row>
            <Col lg={3}>
              <AppsMenu />
            </Col>
            <Col lg={9}>
              <FirebaseCrud />
              {/* <Block
                paddingTop={['10px', '10px', 0]}
                overrides={{
                  Block: {
                    style: {
                      display: 'flex',
                      alignItems: 'center',
                      justifyContent: 'space-between',
                      marginBottom: '40px',
                    },
                  },
                }}
              >
                <Block>
                  <Block
                    as="h3"
                    overrides={{
                      Block: {
                        style: ({ $theme }) => {
                          return {
                            ...$theme.typography.font450,
                            color: $theme.colors.primaryA,
                            marginBottom: '10px',
                          };
                        },
                      },
                    }}
                  >
                    {title}
                  </Block>
                  <Block
                    as="p"
                    overrides={{
                      Block: {
                        style: ({ $theme }) => {
                          return {
                            ...$theme.typography.font200,
                            color: $theme.colors.contentSecondary,
                          };
                        },
                      },
                    }}
                  >
                    {subtitle}
                  </Block>
                </Block>
                <Block overrides={{ Block: { style: { flexShrink: 0 } } }}>
                  <Button
                    onClick={handleAddArticle}
                    overrides={{
                      BaseButton: {
                        style: ({ $theme }) => {
                          return {
                            ...$theme.typography.font250,
                          };
                        },
                      },
                    }}
                  >
                    Add Article
                  </Button>
                </Block>
              </Block>

              <Block
                overrides={{
                  Block: {
                    style: {
                      minHeight: '150px',
                    },
                  },
                }}
              >
                <StyledTable $gridTemplateColumns="130px 200px auto max-content max-content">
                  <StyledTableHeadAlt>Title</StyledTableHeadAlt>
                  <StyledTableHeadAlt>Description</StyledTableHeadAlt>
                  <StyledTableHeadAlt>Slug</StyledTableHeadAlt>
                  <StyledTableHeadAlt>Status</StyledTableHeadAlt>
                  <StyledTableHeadAlt>Action</StyledTableHeadAlt>
                  {articles && articles.length
                    ? articles.map((item, index) => {
                        const striped = index % 2 === 0;
                        if (item && !item.status) return null;
                        const status = item.status;
                        let tag;
                        switch (status) {
                          case 'publish':
                            tag = (
                              <Tag
                                kind="positive"
                                variant="solid"
                                closeable={false}
                              >
                                {status}
                              </Tag>
                            );
                            break;
                          case 'draft':
                            tag = (
                              <Tag
                                kind="neutral"
                                variant="light"
                                closeable={false}
                              >
                                {status}
                              </Tag>
                            );
                            break;
                        }
                        return (
                          <Fragment key={index}>
                            <StyledBodyCell $striped={striped}>
                              {item.title}
                            </StyledBodyCell>
                            <StyledBodyCell $striped={striped}>
                              {item.description}
                            </StyledBodyCell>
                            <StyledBodyCell $striped={striped}>
                              {item.slug}
                            </StyledBodyCell>
                            <StyledBodyCell $striped={striped}>
                              {tag}
                            </StyledBodyCell>
                            <StyledBodyCell $striped={striped}>
                              <IconButton
                                onClick={() => handleUpdateArticle(item)}
                              >
                                <IoIosEye />
                              </IconButton>
                              <IconButton
                                onClick={() => handleDeleteArticle(item.id)}
                              >
                                <AiFillCloseSquare />
                              </IconButton>
                            </StyledBodyCell>
                          </Fragment>
                        );
                      })
                    : null}
                </StyledTable>
              </Block> */}
            </Col>
          </Row>
        </Block>
      </Container>

      <Modal
        onClose={handleModdalClose}
        closeable
        isOpen={visible}
        animate
        size="default"
        role="dialog"
        unstable_ModalBackdropScroll={true}
        overrides={{
          Root: {
            style: () => {
              return { zIndex: 9999 };
            },
          },
        }}
      >
        <ModalHeader>
          {editState ? 'Update Article' : 'Add Article'}
        </ModalHeader>

        <ModalBody style={{ overflow: 'hidden' }}>
          <FlexGrid flexGridColumnCount={1}>
            <FlexGridItem>
              <FormControl
                label="Title"
                overrides={{
                  Label: {
                    style: ({ $theme }) => {
                      return { ...$theme.typography.font200 };
                    },
                  },
                }}
              >
                <Input
                  name="title"
                  value={article.title}
                  onChange={handleOnChange('title')}
                  overrides={{
                    InputContainer: {
                      style: () => {
                        return { backgroundColor: 'transparent' };
                      },
                    },
                  }}
                />
              </FormControl>
            </FlexGridItem>

            <FlexGridItem>
              <FormControl
                label="Description"
                overrides={{
                  Label: {
                    style: ({ $theme }) => {
                      return { ...$theme.typography.font200 };
                    },
                  },
                }}
              >
                <Textarea
                  value={article.description}
                  onChange={handleOnChange('description')}
                  overrides={{
                    InputContainer: {
                      style: () => {
                        return { backgroundColor: 'transparent' };
                      },
                    },
                  }}
                />
              </FormControl>
            </FlexGridItem>

            <FlexGridItem>
              <FormControl
                label="Slug"
                overrides={{
                  Label: {
                    style: ({ $theme }) => {
                      return { ...$theme.typography.font200 };
                    },
                  },
                }}
              >
                <Input
                  value={article.slug}
                  onChange={handleOnChange('slug')}
                  overrides={{
                    InputContainer: {
                      style: () => {
                        return { backgroundColor: 'transparent' };
                      },
                    },
                  }}
                />
              </FormControl>
            </FlexGridItem>

            <FlexGridItem>
              <FormControl
                label="Status"
                overrides={{
                  Label: {
                    style: ({ $theme }) => {
                      return { ...$theme.typography.font200 };
                    },
                  },
                }}
              >
                <Select
                  options={statusOptions}
                  placeholder=""
                  value={article.status}
                  onChange={handleOnChange('status')}
                  overrides={{
                    ControlContainer: {
                      style: () => {
                        return { backgroundColor: 'transparent' };
                      },
                    },
                  }}
                />
              </FormControl>
            </FlexGridItem>

            <FlexGridItem
              overrides={{ Block: { style: { marginTop: '30px' } } }}
            >
              <Button
                onClick={handleOnSubmit}
                overrides={{
                  BaseButton: {
                    style: ({ $theme }) => {
                      return {
                        ...$theme.typography.font250,
                      };
                    },
                  },
                }}
              >
                {editState ? 'Update Article' : 'Add Article'}
              </Button>
              <Button
                kind="minimal"
                onClick={handleModdalClose}
                overrides={{
                  BaseButton: {
                    style: ({ $theme }) => {
                      return {
                        ...$theme.typography.font250,
                      };
                    },
                  },
                }}
              >
                Cancel
              </Button>
            </FlexGridItem>
          </FlexGrid>
        </ModalBody>
      </Modal>
    </>
  );
};

export default FirebaseCRUD;
