import React, { useState } from 'react';
import { NextPage } from 'next';
import Head from 'next/head';
import dynamic from 'next/dynamic';
import { Row, Col } from 'react-flexbox-grid';
import { Block } from 'baseui/block';
import { FlexGrid, FlexGridItem } from 'baseui/flex-grid';
import { FormControl } from 'baseui/form-control';
import { Input } from 'baseui/input';
import { Button } from 'baseui/button';
import Container from '../../components/UiElements/Container/Container';
import SettingsMenu from '../../components/SideMenu/SettingsMenu';
import {
  AvatarWrapper,
  ButtonGroup,
} from '../../components/PageStyles/Settings.styled';

const AvatarWithNoSSR = dynamic(() => import('react-avatar-edit'), {
  ssr: false,
});

const Settings: NextPage<{}> = () => {
  const [state, setState] = useState({
    avatar: '',
    name: '',
    username: '',
    email: '',
  });

  const handleOnChange = (e: any) => {
    const { name, value } = e.target;
    setState({
      ...state,
      [name]: value,
    });
  };

  const handleOnCrop = (img: string) => {
    setState({
      ...state,
      avatar: img,
    });
  };

  const handleBeforeFileLoad = (e: any) => {
    if (e.target.files[0].size > 71680) {
      alert('File is too big!');
      e.target.value = '';
    }
  };

  const handleOnSubmit = () => {
    console.log('Form submit', state);
  };

  const handleResetForm = () => {
    setState({
      avatar: '',
      name: '',
      username: '',
      email: '',
    });
    console.log('Form reset');
  };

  return (
    <>
      <Head>
        <title>Settings | INST.</title>
        <meta name="Description" content="Inst setting page" />
      </Head>

      <Container>
        <Block paddingTop={['15px', '20px', '30px', '40px']}>
          <Row>
            <Col lg={3}>
              <SettingsMenu />
            </Col>
            <Col lg={9}>
              <Block paddingTop={['10px', '10px', '10px', 0]}>
                <FlexGrid
                  flexGridColumnCount={1}
                  flexGridRowGap={['0', '0', '0', 'scale500']}
                >
                  <FlexGridItem>
                    <AvatarWrapper>
                      <AvatarWithNoSSR
                        label="Choose avatar"
                        labelStyle={{ fontSize: '16px', fontWeight: 500 }}
                        width={150}
                        height={150}
                        onCrop={handleOnCrop}
                        onBeforeFileLoad={handleBeforeFileLoad}
                        src={require('../../assets/images/avatar.jpg')}
                      />
                    </AvatarWrapper>
                  </FlexGridItem>
                  <FlexGridItem>
                    <FormControl
                      label="Your Name"
                      caption="Please use 32 characters at maximum"
                      overrides={{
                        Label: {
                          style: ({ $theme }) => {
                            return { ...$theme.typography.font350 };
                          },
                        },
                      }}
                    >
                      <Input
                        name="name"
                        value={state.name}
                        onChange={handleOnChange}
                        placeholder="Please enter your full name or a display name you comfortable with"
                        overrides={{
                          InputContainer: {
                            style: () => {
                              return { backgroundColor: 'transparent' };
                            },
                          },
                        }}
                      />
                    </FormControl>
                  </FlexGridItem>

                  <FlexGridItem>
                    <FormControl
                      label="Your User Name"
                      caption="Please use 16 characters at maximum"
                      error="The user name is already use"
                      overrides={{
                        Label: {
                          style: ({ $theme }) => {
                            return { ...$theme.typography.font350 };
                          },
                        },
                      }}
                    >
                      <Input
                        name="username"
                        placeholder="Please enter your user name or a display name you comfortable with"
                        onChange={handleOnChange}
                        overrides={{
                          InputContainer: {
                            style: () => {
                              return { backgroundColor: 'transparent' };
                            },
                          },
                        }}
                      />
                    </FormControl>
                  </FlexGridItem>

                  <FlexGridItem>
                    <FormControl
                      label="Your Email"
                      caption="johndoe@example.com"
                      overrides={{
                        Label: {
                          style: ({ $theme }) => {
                            return { ...$theme.typography.font350 };
                          },
                        },
                      }}
                    >
                      <Input
                        type="email"
                        name="email"
                        value={state.email}
                        onChange={handleOnChange}
                        placeholder="Please enter your email address"
                        overrides={{
                          InputContainer: {
                            style: () => {
                              return { backgroundColor: 'transparent' };
                            },
                          },
                        }}
                      />
                    </FormControl>
                  </FlexGridItem>

                  <FlexGridItem>
                    <ButtonGroup>
                      <Button
                        onClick={handleOnSubmit}
                        size="large"
                        overrides={{
                          BaseButton: {
                            style: ({ $theme }) => {
                              return {
                                width: '131px',
                                ...$theme.typography.font250,
                              };
                            },
                          },
                        }}
                      >
                        Save
                      </Button>
                      <Button
                        onClick={handleResetForm}
                        kind="tertiary"
                        size="large"
                        overrides={{
                          BaseButton: {
                            style: ({ $theme }) => {
                              return {
                                width: '131px',
                                ...$theme.typography.font250,
                              };
                            },
                          },
                        }}
                      >
                        Cancel
                      </Button>
                    </ButtonGroup>
                  </FlexGridItem>
                </FlexGrid>
              </Block>
            </Col>
          </Row>
        </Block>
      </Container>
    </>
  );
};

export default Settings;
