import React from 'react';
import { withRouter } from 'next/router';
import Header from '../Header/Header';
import Footer from '../Footer/Footer';
import AuthHeader from '../Header/AuthHeader/AuthHeader';
import AuthFooter from '../Footer/AuthFooter/AuthFooter';
import AppWrapper, { ContentWrapper } from './Layout.styled';

const Layout: React.FunctionComponent<{ router?: any }> = ({
  router,
  children,
}) => {
  const pathname = router.pathname;
  let layoutBg = '#ffffff';

  if (pathname === '/') {
    layoutBg = '#fcfcfc';
  }

  return (
    <AppWrapper style={{ backgroundColor: layoutBg }}>
      {pathname === '/login' ||
      pathname === '/signup' ||
      pathname === '/terms' ||
      pathname === '/privacy' ? (
        <AuthHeader pathname={pathname} />
      ) : (
        <Header />
      )}

      <ContentWrapper>{children}</ContentWrapper>

      {pathname === '/login' || pathname === '/signup' ? (
        <AuthFooter pathname={pathname} />
      ) : (
        <Footer />
      )}
    </AppWrapper>
  );
};

export default withRouter(Layout);
