import React, { useState } from 'react';
import { FiMenu, FiMoreHorizontal } from 'react-icons/fi';
import Sticky from 'react-stickynode';
import { useRouter } from 'next/router';
import { Button } from 'baseui/button';
import { Drawer } from 'baseui/drawer';
import Badge from '../../UiElements/Badge/Badge';
import Logo from '../../UiElements/Logo/Logo';
import SvgIcon from '../../UiElements/SvgIcon/SvgIcon';
import TopMenu from '../HeaderMenu/TopMenu/TopMenu';
import MainMenu from '../HeaderMenu/MainMenu/MainMenu';
import AvatarMenu from '../HeaderMenu/AvatarMenu/AvatarMenu';
import HeaderWrapper, {
  PageTitle,
  TopMenuWrapper,
} from './MobileHeader.styled';

const MobileHeader: React.FC<{}> = () => {
  const [isOpen, setIsOpen] = useState(false);
  const [menu, setMenu] = useState('main');
  const { pathname } = useRouter();

  let pageName: React.ReactNode = pathname.split('/').slice(1, 2);

  pageName =
    pathname !== '/' && pathname !== '/_error' ? (
      <PageTitle>{pageName}</PageTitle>
    ) : (
      <Logo
        style={{ marginTop: '3px' }}
        path="/"
        src={require('../../../assets/images/logo.svg')}
      />
    );

  let placement;
  if (menu === 'main') {
    placement = 'left';
  }
  if (menu === 'top') {
    placement = 'right';
  }

  return (
    <Sticky>
      <HeaderWrapper className="mobile">
        <Button
          onClick={() => {
            setIsOpen(true);
            setMenu('main');
          }}
          kind="minimal"
          size="compact"
          overrides={{
            BaseButton: {
              style: () => {
                return { fontSize: '22px' };
              },
            },
          }}
        >
          <FiMenu />
        </Button>

        {pageName}

        <Button
          onClick={() => {
            setIsOpen(true);
            setMenu('top');
          }}
          kind="minimal"
          size="compact"
          overrides={{
            BaseButton: {
              style: () => {
                return { fontSize: '24px' };
              },
            },
          }}
        >
          <FiMoreHorizontal />
        </Button>

        <Drawer
          isOpen={isOpen}
          onClose={() => setIsOpen(false)}
          anchor={placement}
          size="full"
          overrides={{
            Root: {
              style: () => {
                return { zIndex: 9999 };
              },
            },
          }}
        >
          {menu === 'main' && (
            <MainMenu
              className="mobile-menu"
              onClick={() => setIsOpen(false)}
            />
          )}

          {menu === 'top' && (
            <TopMenuWrapper>
              <TopMenu onClick={() => setIsOpen(false)} />
              <Badge
                path="/shop/checkout"
                icon={
                  <SvgIcon
                    src={require('../../../assets/images/cart-bag.svg?include')}
                  />
                }
                count={10}
                style={{ margin: '12px 0px 24px' }}
                onClick={() => setIsOpen(false)}
              />
              <AvatarMenu
                name="Jane Doe"
                src={require('../../../assets/images/avatar.jpg')}
                placement="auto"
                onClick={() => setIsOpen(false)}
              />
            </TopMenuWrapper>
          )}
        </Drawer>
      </HeaderWrapper>
    </Sticky>
  );
};

export default MobileHeader;
