import React from 'react';
import Link from 'next/link';
import { Block } from 'baseui/block';
import Container from '../../UiElements/Container/Container';
import LinkText from './AuthFooter.styled';

type AuthFooterType = {
  pathname?: string;
};

const AuthFooter = ({ pathname }: AuthFooterType) => {
  return (
    <Block
      overrides={{
        Block: {
          style: ({ $theme }) => {
            return {
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              padding: '23px 0',
              borderTop: `1px solid ${$theme.colors.backgroundTertiary}`,
            };
          },
        },
      }}
    >
      <Container>
        <Block
          overrides={{
            Block: {
              style: {
                display: 'flex',
                flexDirection: 'column',
                alignItems: 'center',
                justifyContent: 'center',
              },
            },
          }}
        >
          {pathname === '/login' && (
            <Link href="/signup">
              <LinkText>Donâ€™t have an account? Sign Up</LinkText>
            </Link>
          )}
          {pathname === '/signup' && (
            <>
              <Block paddingBottom="5px">
                <Link href="/login">
                  <LinkText>Already have an account? Log In</LinkText>
                </Link>
              </Block>
              <Block
                overrides={{
                  Block: {
                    style: ({ $theme }) => {
                      return {
                        ...$theme.typography.font200,
                        color: $theme.colors.borderInverseOpaque,
                      };
                    },
                  },
                }}
              >
                By joining, you agree to our{' '}
                <Link href="/terms">
                  <LinkText>Terms of Service</LinkText>
                </Link>{' '}
                and{' '}
                <Link href="/privacy">
                  <LinkText>Privacy Policy</LinkText>
                </Link>
              </Block>
            </>
          )}
        </Block>
      </Container>
    </Block>
  );
};

export default AuthFooter;
