import { NextPage } from 'next';
import { IoIosMailUnread, IoMdCart } from 'react-icons/io';
import { FaMoneyCheckAlt, FaChartLine } from 'react-icons/fa';
import { Row, Col } from 'react-flexbox-grid';
import { Block } from 'baseui/block';
import { Card, StyledBody } from 'baseui/card';
import Container from '../components/UiElements/Container/Container';
import ListGridCard from '../components/UiElements/ListGridCard/ListGridCard';
import LabelGroup from '../components/UiElements/LabelGroup/LabelGroup';
import WidgetCard from '../components/UiElements/WidgetCard/WidgetCard';
import ProductViews from '../containers/Widgets/ProductViews';
import ProductsBar from '../containers/Widgets/ProductsBar';
import Views from '../containers/Widgets/Views';
import CashFlow from '../containers/Widgets/CashFlow';
import gql from 'graphql-tag';
import { useQuery } from '@apollo/react-hooks';
import { withApollo } from '../apollo/client';

const productsBarOptions = [
  {
    color: '#FF0080',
    label: 'Banana',
  },
  {
    color: '#7928CA',
    label: 'Orange',
  },
  {
    color: '#0070F3',
    label: 'Blueberry',
  },
];
const GET_DASHBOARD = gql`
  query {
    dashboard {
      recentApps {
        id
        name
        image
        description
      }
      productViews {
        categories
        products
        views
      }
      productsBar {
        labels
        products
      }
      cashFlow {
        categories
        cash
      }
    }
  }
`;
const Home: NextPage<{}> = () => {
  const { data, loading, error, fetchMore } = useQuery(GET_DASHBOARD, {
    notifyOnNetworkStatusChange: true,
  });
  if (!data) return null;
  const { productViews, recentApps, productsBar, cashFlow } = data.dashboard;
  return (
    <Container>
      <Block paddingTop={['15px', '20px', '30px', '40px']}>
        <Row>
          <Col xs={12} lg={6}>
            <Row>
              <Col sm={6}>
                <WidgetCard
                  style={{ marginBottom: '20px' }}
                  title="210"
                  icon={<IoIosMailUnread color="#ffffff" size="1.7em" />}
                  description="Unread Order Email"
                  btntext="View report"
                  label="Total mail"
                  onClick={() => console.log('View report of unread email.')}
                />
              </Col>
              <Col sm={6}>
                <WidgetCard
                  style={{ marginBottom: '20px' }}
                  color="#0070F3"
                  title="198"
                  icon={<IoMdCart color="#ffffff" size="1.7em" />}
                  description="Pending Orders"
                  btntext="View report"
                  label="Total orders"
                  onClick={() => console.log('View report of pending orders.')}
                />
              </Col>
              <Col sm={6}>
                <WidgetCard
                  style={{ marginBottom: '20px' }}
                  color="#3AA76D"
                  title="$210M"
                  icon={<FaChartLine color="#ffffff" size="1.6em" />}
                  description="Yearly Income"
                  btntext="View report"
                  label="Yearly income"
                  onClick={() => console.log('View report of yearly income.')}
                />
              </Col>
              <Col sm={6}>
                <WidgetCard
                  style={{ marginBottom: '20px' }}
                  color="#7928CA"
                  title="$210M"
                  icon={<FaMoneyCheckAlt color="#ffffff" size="1.6em" />}
                  description="Total Spent"
                  btntext="View report"
                  label="Previous month"
                  onClick={() => console.log('View report of previous month.')}
                />
              </Col>
            </Row>
          </Col>
          <Col xs={12} lg={6}>
            <Card
              title="Views"
              overrides={{
                Root: {
                  style: ({ $theme }) => {
                    return {
                      borderColor: 'transparent',
                      boxShadow: $theme.lighting.shadow400,
                      minHeight: '312px',
                      marginBottom: '20px',
                    };
                  },
                },
                Title: {
                  style: ({ $theme }) => {
                    return {
                      ...$theme.typography.font250,
                      position: 'absolute',
                    };
                  },
                },
                Body: {
                  style: () => {
                    return {
                      minHeight: '260px',
                    };
                  },
                },
              }}
            >
              <StyledBody>
                <ProductViews
                  categories={productViews.categories}
                  products={productViews.products}
                  views={productViews.views}
                />
              </StyledBody>
            </Card>
          </Col>
        </Row>

        <Row>
          <Col xs={12} lg={4}>
            <Card
              title="Multiple Bar"
              overrides={{
                Root: {
                  style: ({ $theme }) => {
                    return {
                      borderColor: 'transparent',
                      boxShadow: $theme.lighting.shadow400,
                      marginBottom: $theme.sizing.scale700,
                    };
                  },
                },
                Title: {
                  style: ({ $theme }) => {
                    return {
                      ...$theme.typography.font250,
                      position: 'absolute',
                    };
                  },
                },
                Body: {
                  style: () => {
                    return {
                      minHeight: '372px',
                      position: 'relative',
                    };
                  },
                },
              }}
            >
              <StyledBody>
                <ProductsBar
                  className="padding-control"
                  labels={productsBar.labels}
                  products={productsBar.products}
                />

                <LabelGroup
                  style={{
                    position: 'absolute',
                    width: '100%',
                    bottom: '-100px',
                  }}
                  items={productsBarOptions}
                />
              </StyledBody>
            </Card>
          </Col>
          <Col xs={12} lg={4}>
            <Card
              title="Recent apps"
              overrides={{
                Root: {
                  style: ({ $theme }) => {
                    return {
                      borderColor: 'transparent',
                      boxShadow: $theme.lighting.shadow400,
                      marginBottom: $theme.sizing.scale700,
                      minHeight: '408px',
                    };
                  },
                },
                Title: {
                  style: ({ $theme }) => {
                    return {
                      ...$theme.typography.font250,
                    };
                  },
                },
              }}
            >
              <StyledBody>
                {recentApps.map((item: any) => (
                  <ListGridCard
                    key={item.id}
                    variant="list"
                    thumb={item.image}
                    title={item.name}
                    description={item.description}
                    style={{
                      alignItems: 'flex-start',
                      marginTop: '20px',
                    }}
                  />
                ))}
              </StyledBody>
            </Card>
          </Col>
          <Col xs={12} lg={4}>
            <Card
              title="Views"
              overrides={{
                Root: {
                  style: ({ $theme }) => {
                    return {
                      borderColor: 'transparent',
                      boxShadow: $theme.lighting.shadow400,
                      marginBottom: $theme.sizing.scale700,
                    };
                  },
                },
                Title: {
                  style: ({ $theme }) => {
                    return {
                      ...$theme.typography.font250,
                      position: 'absolute',
                    };
                  },
                },
                Contents: {
                  style: () => {
                    return {
                      minHeight: '372px',
                    };
                  },
                },
              }}
            >
              <StyledBody>
                <Views className="padding-control" totalView={75} />
              </StyledBody>
            </Card>
          </Col>
        </Row>

        <Row>
          <Col lg={12}>
            <Card
              title="Cash Flow"
              overrides={{
                Root: {
                  style: ({ $theme }) => {
                    return {
                      borderColor: 'transparent',
                      boxShadow: $theme.lighting.shadow400,
                    };
                  },
                },
                Title: {
                  style: ({ $theme }) => {
                    return {
                      ...$theme.typography.font250,
                      position: 'absolute',
                    };
                  },
                },
                Body: {
                  style: () => {
                    return {
                      minHeight: '200px',
                    };
                  },
                },
              }}
            >
              <StyledBody>
                <CashFlow
                  categories={cashFlow.categories}
                  cash={cashFlow.cash}
                />
              </StyledBody>
            </Card>
          </Col>
        </Row>
      </Block>
    </Container>
  );
};

export default withApollo(Home);
