import React, { useState, Fragment } from 'react';
import { Input } from 'baseui/input';
import { Tag } from 'baseui/tag';
import Eliza from 'elizabot';

const eliza = new Eliza();

const Chatbot = () => {
  const [chats, setChats] = useState([]);
  const [value, setValue] = useState('');
  const handleChat = e => {
    console.log(e.key, 'what key');
    if (e.key === 'Enter') {
      console.log('here');
      const reply = eliza.transform(value);
      chats.push({ id: Date.now(), message: value });
      chats.push({ id: Date.now(), message: reply });
      setChats(chats);
    }
  };
  console.log(chats, 'what chats');
  return (
    <div>
      <Input
        onChange={e => setValue(e.currentTarget.value)}
        onKeyPress={handleChat}
      />
      <ShowChats chats={chats} />
    </div>
  );
};

const ShowChats = ({ chats }) => {
  return (
    <Fragment>
      {chats && chats.length
        ? chats.map(chat => (
            <div key={chat.id}>
              <Tag>{chat.message}</Tag>
            </div>
          ))
        : null}
    </Fragment>
  );
};

export default Chatbot;
