import React, { useState } from 'react';
import { NextPage } from 'next';
import Head from 'next/head';
import { Row, Col } from 'react-flexbox-grid';
import { Block } from 'baseui/block';
import Container from '../../components/UiElements/Container/Container';
import ChartMenu from '../../components/SideMenu/ChartMenu';
import ApexChart from '../../components/UiElements/ApexChart/ApexChart';

const Chart: NextPage<{}> = () => {
  const [state, setState] = useState<any>({
    series: [
      {
        name: 'High - 2020',
        data: [28, 29, 33, 36, 32, 32, 33],
      },
      {
        name: 'Low - 2020',
        data: [12, 11, 14, 18, 17, 13, 13],
      },
    ],
    options: {
      chart: {
        height: 420,
        type: 'line',
        dropShadow: {
          enabled: true,
          color: '#000',
          top: 18,
          left: 7,
          blur: 10,
          opacity: 0.2,
        },
        toolbar: {
          show: false,
        },
      },
      colors: ['#ff0080', '#006ff3'],
      dataLabels: {
        enabled: true,
      },
      stroke: {
        curve: 'smooth',
      },
      title: {
        text: 'Line Chart',
        align: 'left',
      },
      markers: {
        size: 1,
      },
      xaxis: {
        categories: ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun', 'Jul'],
        title: {
          text: 'Month',
        },
      },
      yaxis: {
        title: {
          text: 'Revenue',
        },
        min: 5,
        max: 40,
      },
      legend: {
        position: 'top',
        horizontalAlign: 'right',
        floating: true,
        offsetY: -25,
        offsetX: -5,
      },
    },
  });

  return (
    <>
      <Head>
        <title>Chart | INST.</title>
        <meta name="Description" content="Inst chart app" />
      </Head>

      <Container>
        <Block paddingTop={['15px', '20px', '30px', '40px']}>
          <Row>
            <Col lg={3}>
              <ChartMenu />
            </Col>
            <Col lg={9}>
              <Block paddingTop={['10px', '10px', 0]}>
                <ApexChart
                  options={state.options}
                  series={state.series}
                  type="line"
                  height={420}
                />
              </Block>
            </Col>
          </Row>
        </Block>
      </Container>
    </>
  );
};

export default Chart;
